import React, { useState } from "react";
import axios from "axios";

export default function WeatherApp() {
  const [ready, setReady] = useState(false);
  const [temperature, setTemperature] = useState(null);
  function handleResponse(response) {
    console.log(response.data);
    setTemperature(response.data.main.temp);
  }

  if (ready) {
    return (
      <div className="col-search">
        <form id="search-location-form">
          <input type="text" placeholder="" id="search-text-input" />

          <input
            id="search-button"
            type="image"
            name="submit"
            src="images/SEARCH.png"
            alt="Submit"
          />

          <button id="current-location-button">
            <img
              id="current-location-button-image"
              src="images/CURRENT_LOCATION.png"
              alt=""
            />
          </button>
        </form>
      </div>
    );

  } else {
    const apiKey = "51ac8c007be10df8c6279a4b5212951e";
 let city = "London";
 let apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`
 axios.get(apiUrl).then(handleResponse);

 return "Loading..."
  }

}
