import React, { useState } from "react";

export default function UnitConversion(props) {
  const [unit, setUnit] = useState("celsius");

  function showFahrenheit(event) {
    event.preventDefault();
    setUnit("fahrenheit");
  }

  function showCelsius(event) {
    event.preventDefault();
    setUnit("celsius");
  }

  if (unit === "celsius") {
    return (
      <span className="unit-conversion">
        <span className="unit">{Math.round[props.celsius]}</span>
        <span id="celsius-link">째C
        <span className="divider"> | </span>
        <a href="/" id="fahrenheit-link" onClick={showFahrenheit}>
          째F
        </a>
      </span>
      </span>
    );
  } else {
      let fahrenheit = (props.celsius * 9/5) + 32;
    return (
      <span className="unit-conversion">
        <span className="unit">{Math.round[fahrenheit]}</span>
        <a href="/" id="celsius-link" onClick={showCelsius}>
          째C
        </a>
        <span className="divider"> | </span>
        <span id="fahrenheit-link">째F</span>
      </span>
    );
  }
}
