import React, { useState } from "react";
import WeatherDisplay from "./WeatherDisplay";
import axios from "axios";

export default function WeatherApp(props) {
  const [weatherData, setWeatherData] = useState({ ready: false });
  const [city, setCity] = useState(props.defaultCity);
  function handleResponse(response) {
    setWeatherData({
      ready: true,
      description: response.data.weather[0].description,
      wind: Math.round(response.data.wind.speed),
      humidity: Math.round(response.data.main.humidity),
      city: response.data.name,
      date: new Date(response.data.dt * 1000),
      time: new Date(),
      icon: response.data.weather[0].icon,
      temperature: Math.round(response.data.main.temp),
    });
  }

  function search() {
    const apiKey = "51ac8c007be10df8c6279a4b5212951e";
    let apiUrl = `http://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`;
    axios.get(apiUrl).then(handleResponse);

  }

  function handleSubmit(event) {
    event.preventDefault();
    search();
  }

  function handleCitySearch(event) {
    setCity(event.target.value);
  }

  if (weatherData.ready) {
    return (
      <div className="weather-app">
        <div className="col-search">
          <form id="search-location-form" onSubmit={handleSubmit}>
            <input type="text" placeholder="" id="search-text-input" />

            <input
              id="search-button"
              type="image"
              name="submit"
              src="images/SEARCH.png"
              alt="Submit"
              onSearch="handleCitySearch"
            />

            <button id="current-location-button">
              <img
                id="current-location-button-image"
                src="images/CURRENT_LOCATION.png"
                alt=""
              />
            </button>
          </form>
          <br />

          <WeatherDisplay data={weatherData} />
        </div>
      </div>
    );
  } else {

    return "Loading...";
  }
}
